import { useNavigation } from "@react-navigation/core";
import {
  Button,
  Text,
  TextInput,
  View,
  TouchableOpacity,
  Alert,
} from "react-native";
import { useState } from "react";
import axios from "axios";

export default function SignInScreen({ setToken }) {
  const navigation = useNavigation();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isVisible, setIsVisible] = useState(false);

  const handleSignInSubmit = async () => {
    if (email && password) {
      try {
        const response = await axios.post(
          "https://express-airbnb-api.herokuapp.com/user/log_in",
          { email, password }
        );
        response && Alert.alert("Connexion r√©ussie !");
      } catch (error) {
        Alert.alert("Utilisateur non reconnu", "Veuillez vous inscrire");
      }
    } else if (email && !password) {
      Alert.alert("Veuillez renseigner un mot de passe");
    } else if (!email && password) {
      Alert.alert("Veuillez renseigner une adresse email");
    }
  };

  return (
    <View>
      <View>
        <Text>Email: </Text>
        <TextInput
          placeholder="Email"
          onChangeText={(mail) => {
            setEmail(mail);
          }}
        />
        <Text>Password: </Text>
        <TextInput
          placeholder="Password"
          secureTextEntry={true}
          onChangeText={(pass) => setPassword(pass)}
        />
        <View style={styles.passInput}></View>
        <TouchableOpacity>
          <Text>üëÅ </Text>
        </TouchableOpacity>
        <Button title="Sign in" onPress={handleSignInSubmit} />
        <TouchableOpacity
          onPress={() => {
            navigation.navigate("SignUp");
          }}
        >
          <Text>Create an account</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

